import React, { useState } from "react";
import { Sun, Moon, BatteryFull, Zap, Tv2, Snowflake, Utensils, Car, Refrigerator } from "lucide-react";
import "./App.css"; // Add custom CSS or animations if needed
import './index.css';

const solarGeneration = [
  0, 0, 0, 0, 0, 1, 3, 6, 8, 10, 11, 12,
  12, 11, 9, 6, 3, 1, 0, 0, 0, 0, 0, 0
];

const homeUsage = [
  1, 1, 1, 1, 1, 2, 3, 3, 4, 5, 5, 4,
  4, 5, 6, 5, 4, 4, 4, 4, 3, 2, 2, 1
];

const applianceStatus = (hour) => ({
  fridge: true, // always on
  tv: hour >= 18 && hour <= 22,
  aircon: hour >= 12 && hour <= 17,
  oven: hour === 19,
  car: hour >= 0 && hour <= 6
});

export default function App() {
  const [hour, setHour] = useState(12);
  const [withBattery, setWithBattery] = useState(true);

  const solar = solarGeneration[hour];
  const usage = homeUsage[hour];
  const appliances = applianceStatus(hour);

  let battery = 0;
  let grid = 0;

  if (withBattery) {
    if (solar >= usage) {
      battery = Math.min(100, (solar - usage) * 10);
    } else {
      battery = Math.max(0, 100 - (usage - solar) * 5);
    }
  } else {
    grid = usage - solar;
    if (grid < 0) grid = 0;
  }

  return (
    <div className="p-4 max-w-md mx-auto font-sans text-gray-800">
      <h1 className="text-2xl font-bold mb-2 text-center">☀️ A Day in the Life</h1>
      <p className="text-center mb-4 text-sm text-gray-600">
        Explore how your solar + battery system behaves throughout the day.
      </p>

      {/* Sun / Moon Animation */}
      <div className="flex justify-center mb-4">
        {hour >= 6 && hour <= 18 ? <Sun className="text-yellow-500" /> : <Moon className="text-indigo-400" />}
      </div>

      {/* Slider */}
      <div className="mb-4">
        <input
          type="range"
          min="0"
          max="23"
          value={hour}
          onChange={(e) => setHour(Number(e.target.value))}
          className="w-full accent-green-500"
        />
        <p className="text-center text-sm mt-1">Hour: {hour}:00</p>
      </div>

      {/* Toggle */}
      <div className="mb-4 text-center">
        <button
          onClick={() => setWithBattery(!withBattery)}
          className={`px-4 py-2 rounded-full text-white font-medium transition-all ${withBattery ? 'bg-green-600' : 'bg-gray-400'}`}
        >
          {withBattery ? "With Battery" : "Without Battery"}
        </button>
      </div>

      {/* Energy Info */}
      <div className="grid grid-cols-1 gap-4">
        <div className="p-4 border rounded-lg shadow text-center">
          <Sun className="mx-auto text-yellow-500" />
          <p className="text-xs text-gray-500">Solar Output</p>
          <p className="text-lg font-semibold">{solar} kWh</p>
        </div>
        <div className="p-4 border rounded-lg shadow text-center">
          <BatteryFull className="mx-auto text-green-500" />
          <p className="text-xs text-gray-500">Battery Level</p>
          <div className="w-full bg-gray-200 h-3 rounded mt-1 mb-1">
            <div
              className="bg-green-500 h-3 rounded"
              style={{ width: `${battery}%`, transition: "width 0.5s" }}
            ></div>
          </div>
          <p className="text-sm">{withBattery ? `${battery}%` : "N/A"}</p>
        </div>
        <div className="p-4 border rounded-lg shadow text-center">
          <Zap className="mx-auto text-blue-500" />
          <p className="text-xs text-gray-500">Grid Usage</p>
          <p className="text-lg font-semibold">
            {withBattery ? (usage > solar ? `${usage - solar} kWh` : "0 kWh") : `${grid} kWh`}
          </p>
        </div>
      </div>

      {/* Appliances */}
      <div className="mt-6 text-center">
        <p className="font-semibold mb-2">Appliances Active at {hour}:00</p>
        <div className="grid grid-cols-3 gap-4 text-sm">
          <div className={appliances.fridge ? "text-green-600" : "text-gray-400"}>
            <Refrigerator className="mx-auto" />
            Fridge
          </div>
          <div className={appliances.tv ? "text-green-600" : "text-gray-400"}>
            <Tv2 className="mx-auto" />
            TV
          </div>
          <div className={appliances.aircon ? "text-green-600" : "text-gray-400"}>
            <Snowflake className="mx-auto" />
            Aircon
          </div>
          <div className={appliances.oven ? "text-green-600" : "text-gray-400"}>
            <Utensils className="mx-auto" />
            Oven
          </div>
          <div className={appliances.car ? "text-green-600" : "text-gray-400"}>
            <Car className="mx-auto" />
            EV Car
          </div>
        </div>
      </div>
    </div>

  );
}
